(function($){$.fn.get_num=function(type){$(this).hoverDelay(function(){if($(this).find(".left:eq(0)").html()==''){var id=$(this).attr("data-id");var that=$(this);var url=msc.tools.getAjaxUrl({ac:"commondata",op:"get_common_data_num"});$.ajax({type:"GET",url:url,data:{id:id,type:type},dataType:"json",success:function(data){if(data.error===0){that.find(".left:eq(0)").html("<span>阅读（"+data.data.readnum+"）</span><span>收藏（"+data.data.collnum+"）</span><span>喜欢（"+data.data.likenum+"）</span>").show();if(type=="collect"){that.find("div.collect_num").show();that.find("span.collect_num_span").html(data.data.recipenum+"篇菜谱").show();};}}});}else{$(this).find(".left:eq(0)").show();if(type=="collect"){$(this).find("span.collect_num_span").show();$(this).find("div.collect_num").show();}}},function(){$(this).find(".left:eq(0)").hide();if(type=="collect"){$(this).find("span.collect_num_span").hide();$(this).find("div.collect_num").hide();}},150,100);};})(jQuery);